import React, { useState, useEffect } from 'react';

const ModalData = ({ isOpen, onClose, onSubmit, initialData = {}, isUpdateMode }) => {
  const [formData, setFormData] = useState({
    country: '',
    year: '',
    ghi: '',
    child_stunting: '',
    child_wasting: '',
    undernourishment: '',
    child_mortality: ''
  });

  useEffect(() => {
    if (isOpen) {
      if (isUpdateMode && initialData) {
        setFormData(initialData);
      } else {
        setFormData({
          country: '',
          year: '',
          ghi: '',
          child_stunting: '',
          child_wasting: '',
          undernourishment: '',
          child_mortality: ''
        });
      }
    }
  }, [isOpen, initialData, isUpdateMode]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // 간단한 폼 검증
    if (!formData.country || !formData.year || !formData.ghi) {
      alert('모든 필수 입력 필드를 작성해주세요.');
      return;
    }

    onSubmit(formData);
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={() => onClose()}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <h2>{isUpdateMode ? 'Update GHI Record' : 'Add GHI Record'}</h2>
        <form onSubmit={handleSubmit}>
          <label>Country:</label>
          <input
            name="country"
            value={formData.country}
            onChange={handleChange}
            placeholder="Enter country name"
            required
          />

          <label>Year:</label>
          <input
            name="year"
            type="number"
            value={formData.year}
            onChange={handleChange}
            placeholder="Enter year"
            required
          />

          <label>GHI:</label>
          <input
            name="ghi"
            type="number"
            step="0.1"
            value={formData.ghi}
            onChange={handleChange}
            placeholder="Enter GHI value"
            required
          />

          <label>Child Stunting:</label>
          <input
            name="child_stunting"
            type="number"
            step="0.1"
            value={formData.child_stunting}
            onChange={handleChange}
            placeholder="Enter stunting percentage"
          />

          <label>Child Wasting:</label>
          <input
            name="child_wasting"
            type="number"
            step="0.1"
            value={formData.child_wasting}
            onChange={handleChange}
            placeholder="Enter wasting percentage"
          />

          <label>Undernourishment:</label>
          <input
            name="undernourishment"
            type="number"
            step="0.1"
            value={formData.undernourishment}
            onChange={handleChange}
            placeholder="Enter undernourishment percentage"
          />

          <label>Child Mortality:</label>
          <input
            name="child_mortality"
            type="number"
            step="0.1"
            value={formData.child_mortality}
            onChange={handleChange}
            placeholder="Enter mortality percentage"
          />

          <button type="submit">{isUpdateMode ? 'Update' : 'Add'}</button>
          <button type="button" onClick={onClose}>
            Cancel
          </button>
        </form>
      </div>
    </div>
  );
};

export default ModalData;
