import React, { useState, useEffect } from 'react';
import './modal.css'
const ModalData = ({ isOpen, onClose, onSubmit, initialData = {}, isUpdateMode }) => {
  const [formData, setFormData] = useState({
    country: '',
    year: '',
    ghi: '',
    child_stunting: '',
    child_wasting: '',
    undernourishment: '',
    child_mortality: ''
  });

  useEffect(() => {
    if (isUpdateMode && initialData) {
      setFormData(initialData);
    } else {
      setFormData({
        country: '',
        year: '',
        ghi: '',
        child_stunting: '',
        child_wasting: '',
        undernourishment: '',
        child_mortality: ''
      });
    }
  }, [isUpdateMode, initialData, isOpen]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  // 모달이 열리지 않은 경우 아무것도 렌더링하지 않음
  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <h2>{isUpdateMode ? 'Update GHI Record' : 'Add GHI Record'}</h2>
        <form onSubmit={handleSubmit}>
          <label>Country:</label>
          <input
            name="country"
            value={formData.country}
            onChange={handleChange}
            required
          />

          <label>Year:</label>
          <input
            name="year"
            type="number"
            value={formData.year}
            onChange={handleChange}
            required
          />

          <label>GHI:</label>
          <input
            name="ghi"
            type="number"
            step="0.1"
            value={formData.ghi}
            onChange={handleChange}
            required
          />

          <label>Child Stunting:</label>
          <input
            name="child_stunting"
            type="number"
            step="0.1"
            value={formData.child_stunting}
            onChange={handleChange}
          />

          <label>Child Wasting:</label>
          <input
            name="child_wasting"
            type="number"
            step="0.1"
            value={formData.child_wasting}
            onChange={handleChange}
          />

          <label>Undernourishment:</label>
          <input
            name="undernourishment"
            type="number"
            step="0.1"
            value={formData.undernourishment}
            onChange={handleChange}
          />

          <label>Child Mortality:</label>
          <input
            name="child_mortality"
            type="number"
            step="0.1"
            value={formData.child_mortality}
            onChange={handleChange}
          />

          <button type="submit">{isUpdateMode ? 'Update' : 'Add'}</button>
          <button type="button" onClick={onClose}>
            Cancel
          </button>
        </form>
      </div>
    </div>
  );
};

export default ModalData;
