const UpdateData = async (id, formData, refreshData) => {
  try {
    const response = await fetch(
      `https://6743ce15b7464b1c2a65e803.mockapi.io/GHI/${id}`, // ID 포함 URL
      {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData), // 수정할 데이터
      }
    );

    if (response.ok) {
      console.log('데이터 업데이트 성공:', id);
      refreshData(); // 수정 후 데이터 갱신
    } else {
      console.error(`데이터 업데이트 실패: ${response.status}`);
    }
  } catch (error) {
    console.error('데이터 업데이트 중 오류 발생:', error);
  }
};
