import React, { useState, useEffect } from 'react';
import './modal.css';

const ModalData = ({ isOpen, onClose, onSubmit, initialData = {}, isUpdateMode }) => {
  const [formData, setFormData] = useState({
    country: '',
    year: '',
    ghi: '',
  });

  useEffect(() => {
    if (isUpdateMode && initialData) {
      setFormData(initialData); // 초기값 설정
    } else {
      setFormData({ country: '', year: '', ghi: '' });
    }
  }, [isUpdateMode, initialData]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <h2>{isUpdateMode ? 'Update Record' : 'Add Record'}</h2>
        <form onSubmit={handleSubmit}>
          <label>
            Country:
            <input
              name="country"
              value={formData.country}
              onChange={handleChange}
              required
            />
            <br/>
          </label>
          <label>
            Year:
            <input
              name="year"
              type="number"
              value={formData.year}
              onChange={handleChange}
              required
            />
          </label>
          <br/>
          <label>
            GHI:
            <input
              name="ghi"
              type="number"
              step="0.1"
              value={formData.ghi}
              onChange={handleChange}
              required
            />
          </label>
          <button type="submit">{isUpdateMode ? 'Update' : 'Add'}</button>
          <button type="button" onClick={onClose}>
            Cancel
          </button>
        </form>
      </div>
    </div>
  );
};

export default ModalData;
