const UpdateData = async (id, formData, refreshData) => {
  try {
    const response = await fetch(
      `https://6743ce15b7464b1c2a65e803.mockapi.io/GHI/${id}`,
      {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      }
    );
    if (response.ok) {
      console.log('데이터 수정 성공');
      refreshData(); // 데이터 갱신
    } else {
      console.error(`데이터 수정 실패: ${response.status}`);
    }
  } catch (error) {
    console.error('데이터 수정 중 오류 발생:', error);
  }
};
