import React, { useState, useEffect } from 'react';
import ReadData from './components/ReadData';
import ModalData from './components/ModalData';
import CreateData from './components/CreateData';
import UpdateData from './components/UpdateData';
import DeleteData from './components/DeleteData';

const App = () => {
  const [GHIData, setGHIData] = useState([]); // GHI 데이터 상태
  const [isModalOpen, setIsModalOpen] = useState(false); // 모달 상태
  const [currentData, setCurrentData] = useState(null); // 선택된 데이터
  const [isUpdateMode, setIsUpdateMode] = useState(false); // 업데이트 모드 여부

  // 데이터 읽기 함수
  const fetchGHI = async () => {
    try {
      const response = await fetch('https://6743ce15b7464b1c2a65e803.mockapi.io/GHI');
      const data = await response.json();
      setGHIData(data);
    } catch (error) {
      console.error('데이터를 가져오는 중 오류 발생:', error);
    }
  };

  // 모달 열기
  const openModal = (data = null, updateMode = false) => {
    setCurrentData(data);
    setIsUpdateMode(updateMode);
    setIsModalOpen(true);
  };

  // 모달 닫기
  const closeModal = () => {
    setCurrentData(null);
    setIsModalOpen(false);
  };

  // 데이터 추가
  const handleCreate = async (formData) => {
    try {
      await CreateData(formData, fetchGHI);
      closeModal();
    } catch (error) {
      console.error('데이터 생성 중 오류 발생:', error);
    }
  };

  // 데이터 업데이트
  const handleUpdate = async (formData) => {
    if (currentData?.id) {
      try {
        await UpdateData(currentData.id, formData, fetchGHI);
        closeModal();
      } catch (error) {
        console.error('데이터 업데이트 중 오류 발생:', error);
      }
    }
  };

  // 데이터 삭제
  const handleDelete = async (id) => {
    try {
      await DeleteData(id, fetchGHI);
    } catch (error) {
      console.error('데이터 삭제 중 오류 발생:', error);
    }
  };

  // 초기 데이터 로드
  useEffect(() => {
    fetchGHI();
  }, []);

  return (
    <div>
      <h1>GHI Management System</h1>
      <button onClick={() => openModal()}>Add New Record</button>

      {/* 데이터 읽기 및 관리 */}
      <ReadData
        GHI={GHIData}
        fetchGHI={fetchGHI}
        onEdit={(data) => openModal(data, true)} // 수정 시 모달 열기
        onDelete={(id) => handleDelete(id)}
      />

      {/* 데이터 추가/수정 모달 */}
      <ModalData
        isOpen={isModalOpen}
        onClose={closeModal}
        onSubmit={isUpdateMode ? handleUpdate : handleCreate}
        initialData={currentData}
        isUpdateMode={isUpdateMode}
      />
    </div>
  );
};

export default App;
