// App.js
import React, { useState } from 'react';
import deleteGHI from '../components/deleteData';
import updateGHI from '../components/updateData';
import './App.css';

const App = () => {
  const [GHI, setGHI] = useState([]); // 기존 students를 GHI로 변경
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isUpdateMode, setIsUpdateMode] = useState(false);
  const [currentData, setCurrentData] = useState(null); // currentStudent를 currentData로 변경

  const fetchGHI = async () => { // fetchStudents를 fetchGHI로 변경
    const response = await fetch('https://6743ce15b7464b1c2a65e803.mockapi.io/GHI');
    const data = await response.json();
    setGHI(data); // setStudents를 setGHI로 변경
  };

  const handleAddData = () => { // handleAddStudent를 handleAddData로 변경
    setIsUpdateMode(false);
    setCurrentData(null);
    setIsModalOpen(true);
  };

  const handleEditData = (data) => { // handleEditStudent를 handleEditData로 변경
    setIsUpdateMode(true);
    setCurrentData(data);
    setIsModalOpen(true);
  };

  const handleModalSubmit = (formData) => {
    if (isUpdateMode) {
      updateGHI(formData.id, formData, fetchGHI); // updateStudent를 updateGHI로 변경
    } else {
      createGHI(formData, fetchGHI); // createStudent를 createGHI로 변경
    }
    setIsModalOpen(false);
  };

  const handleDeleteData = (id) => { // handleDeleteStudent를 handleDeleteData로 변경
    deleteData(id, fetchGHI); // deleteStudent를 deleteData로 변경
  };

  return (
    <div>
      <h1>GHI 관리 시스템</h1> {/* 학생 관리 시스템을 GHI 관리 시스템으로 변경 */}
      <button className='addbutton' onClick={handleAddData}>Add Data</button> {/* Add Student를 Add Data로 변경 */}
      <ReadGHI
        GHI={GHI} // students를 GHI로 변경
        fetchGHI={fetchGHI} // fetchStudents를 fetchGHI로 변경
        onEdit={handleEditData} // onEditStudent를 onEditData로 변경
        onDelete={handleDeleteData} // onDeleteStudent를 onDeleteData로 변경
      />
      {isModalOpen && (
        <GHIEditModal // StudentModal을 GHIEditModal로 변경
          isOpen={isModalOpen}
          onClose={() => setIsModalOpen(false)}
          onSubmit={handleModalSubmit}
          initialData={currentData} // currentStudent를 currentData로 변경
          isUpdateMode={isUpdateMode}
        />
      )}
    </div>
  );
};

export default App;
