const updateGHI = async (country, year, formData, refreshData) => {
  try {
    const response = await fetch(
      `https://6743ce15b7464b1c2a65e803.mockapi.io/GHI?country=${country}&year=${year}`,
      {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      }
    );

    if (response.ok) {
      console.log('업데이트 성공');
      refreshData();
    } else {
      console.error(`업데이트 실패: ${response.status}`);
    }
  } catch (error) {
    console.error('업데이트 중 오류 발생:', error);
  }
};

export default updateGHI;
